// components/PopularDestinations.jsx  (same idea for TopSellingPackages.jsx)
import { Grid, Skeleton, Typography, Alert } from '@mui/material';
import DestinationCard from './DestinationCard';
import { useDestinations } from '../hooks/useDestinations';

export default function PopularDestinations() {
  const { data, isLoading, isError, error } = useDestinations();

  if (isError) return <Alert severity="error">{error?.message || 'Failed to load'}</Alert>;

  const items = isLoading ? Array.from({ length: 6 }) : (data ?? []);

  return (
    <>
      <Typography variant="h4" mb={2}>
        Explore Most Popular Destinations
      </Typography>

      <Grid container spacing={3}>
        {items.map((item, idx) => (
          <Grid
            key={idx}
            item
            xs={12}   // full‑width on phones
            sm={4}    // 3‑per‑row on ≥600 px (12 ÷ 4 = 3)
          >
            {isLoading ? (
              <Skeleton variant="rectangular" width="100%" height={260} sx={{ borderRadius: 3 }} />
            ) : (
              <DestinationCard dest={item} />
            )}
          </Grid>
        ))}
      </Grid>
    </>
  );
}
